## Code Example
```c
/* Basic heapsort

   Copyright (C) 2020 Embecosm Limited <www.embecosm.com>
   Contributor: Jeremy Bennett <jeremy.bennett@embecosm.com>
   SPDX-License-Identifier: GPL-3.0-or-later */

#include <stdlib.h>
#include <stdio.h>

#ifndef N
#define N 20
#endif

void
populate (int arr[])
{
  for (int i = 0; i < N; i++)
    arr[i] = rand () % N;
}

void
swap (int *a, int *b)
{
  int t = *a;
  *a = *b;
  *b = t;
}

void
dump_array (int arr[])
{
  for (int i = 0; i < N; i++)
    printf ("%2d ", arr[i]);

  printf ("\n");
}

void
heapify (int *a, int i, int n)
{
  int lc = 2 * i + 1;		/* Index of left child */
  int rc = 2 * i + 2;		/* Index of right child */

  if (lc <= (n - 1))		/* Not a leaf) */
    {
      int  k;			/* Larger child (if > a[i]) */
      if (rc > (n - 1))
	if (a[lc] > a[i])
	  k = lc;		/* Swap left child */
	else
	  return;		/* Heap in order */
      else if ((a[lc] > a[i]) && (a[lc] >= a[rc]))
	k = lc;			/* Swap left child */
      else if (a[rc] >= a[i])
	k = rc;			/* Swap right child */
      else
	return;			/* Heap in order */

      swap ((&a[i]), &(a[k]));	/* Swap the larger child */
      dump_array (a);
      heapify (a, k, n);
    }
}

void
buildheap (int *a)
{
  int i;

  for (i = N - 1; i >= 0; i--)
    heapify (a, i, N);
}

int
main ()
{
  int a[N];
  int i;

  srand (561U);

  populate (a);
  dump_array (a);

  buildheap (a);
  printf ("\n");

  for (i = N - 1; i >= 0; i--)
    {
      swap (&(a[0]), &(a[i]));
      heapify (a, 0, i);
    }
  dump_array (a);
  return 0;
}

